INSERT INTO `hubungi` (`id_hubungi`, `nama`, `email`, `subjek`, `pesan`, `tanggal`) VALUES
(11, 'dewi fatimah - ciptamedia - Indonesia Public Servi', 'inpus.ciptamedia@gmail.com', 'Informasi Pameran Pelayanan Publik', ' Ciptamedia  akan  menyelenggarakan  pameran  pelayanan  publik  dengan  judul  â€œIndonesia  Public  Service  2015â€  pada  tanggal  4-7  Juni  2015  di  Bandung  Convention  Centre  (BCC)  Jl.  Soekarno  Hatta  no.  354  Bandung.  Informasi  lebih  lanjut  hub  :  Dewi  Fatimah  081809986659  /  telefon  022  7321021  ext  105  web  www.ciptamedia.co.id.  Harapan  kami  mendapatkan  respon  dan  tanggapan  partisipasi  di  kegiatan  ini  terimakasih   ', '2015-02-20'),
(12, 'Widodo', 'widodo@biotrop.org', 'Undangan Pelatihan Kepada Panglima Kodam 16 Pattimura', ' Yth.  Bapak/Ibu,\r\n\r\nMohon  informasi  alamat  email  untuk  pengiriman  undangan  kegiatan  pelatihan.  Kami  sudah  mencoba  mengirim  melalui  info@kodam16pattimura.mil.id  namun  tidak  dapat  terkirim.  \r\n\r\nTerima  kasih   ', '2015-09-18'),
(13, 'Lien', 'olienbowens@gmail.com', 'Diskusi', ' Ass,,,,  saya  mau  menanyakan  seputar  Anggota  TNI,  saya  cuman  manyarakat  biasa  .  apa  bisa  tanya-tanya  ?.  makasih  sebelumnya.   ', '2015-09-28'),
(14, 'Triyudha Ichwan', 'triyudha@gmail.com', 'Susunan Kepala Staf Kodam Pattimura', ' Dengan  hormat,\r\n\r\nSaya  putra  dr  alm  Brigjen  Lukmanul  Ichwan  Anwar,  sekitar  tahun  1970  -  1973  ayah  saya  menjabat  sebagai  Kasdam  (dulu)  XV  Pattimura.  Ketika  itu  yang  menjabat  sebagai  Pangdam  adalah  Brigjen  Wing  Wiryawan.\r\n\r\nSaya  cari  sejarahnya  disini  sama  sekali  tidak  ketemu.  Apakah  karena  Kodam  Pattimura  sekarang  sudah  menjadi  Kodam  XVI  maka  sejarah  tentang  Kodam  XV  ditiadakan?\r\n\r\nTerima  kasih  atas  tanggapannya.\r\n\r\nHormat  saya\r\nTriyudha  Ichwan   ', '2015-09-30'),
(15, 'Sony Setio Koesworo', 'sonyraptor1@gmail.com', 'Koordinasi', ' Selamat  Siang,\r\n\r\nDengan  Hormat  \r\nKami  Bermaksud  Mengadakan  Audiensi  Dengan  Bapak  Pangdam  XVI  Pattimura  Terkait  Kegiatan  Sarasehan  yang  Bekerjasama  Dengan  Direktorat  Kewaspadaan  Nasional  Kemendagri  RI.\r\n\r\nMohon  Ijin  Untuk  Memohon  Alamat  Kantor  dan  Contac  Person  guna  Memasukan  Syarat Administrasi.\r\n\r\nDUMP.\r\n r\nHormat Kami  Lembaga  Sentra  Wawasan  dan  Analisa  Terpadu Indonesia\r\n(SWAT-INDONESIA)\r \n\r\nMalang 7  Oktober  2015\r\n\r\n\r\nSony  Setio  Koesworo  ,ST\r\nKetua   ', '2015-10-07'),
(16, 'MARTIN BOY HANSEN BUTAR BUTAR', 'martinboyhansen@ristek.go.id', 'Ijin Koordinasi', ' Selamat  Siang  Bapak  dan  Ibu.  Pak/Bu,  perkenalkan  saya  Martin  Boy  Hansen  Butar-Butar.  Saya  staf  di  Direktorat  Pengembangan  Teknologi  Industri,  pada  Dirjen  Penguatan  Riset  dan  Pengembangan  di  Kementerian  Riset,  Teknologi,  dan  Pendidikan  Tinggi.  saya  mendapat  tugas  untuk  berkoordinasi  dengan  Seppri  Pangdam  Pattimura  untuk  mengatur  jadwal  audiensi  antara  Direktur  Pengembangan  Teknologi  Industri  Kemenristekdikti  dengan  Pangdam  Pattimura  dalam  rangka  menindaklanjuti  pertemua  antara  Menteri  Ristekdikti  dan  Pangdam  Pattimura  yang  membahas  tentang  emas  biru.  Kami  ingin  mendapatkan  kontak  Seppri  Pangdam  Pattimura  suapay  bisa  berkomunikasi  mengatur  jadwal.  Demikian  yang  dapat  kami  sampaikan  dan  kami  ucapkan  terimakasih  atas  perhatiannya.   ', '2015-10-23');
